// loadmore
$(function () {
    $(".mini-post").slice(0,4).show();
    $("#loadMore").on('click', function (e) {
        e.preventDefault();
        $(".mini-post:hidden").slice(0,4).show();
        if ($(".mini-post:hidden").length === 0) {
            $("#loadMore").hide();
            $("#loadless").css('display','flex').show();
            
        }
    });
        $("#loadless").on('click', function (e) {
        e.preventDefault();
        $(".mini-post:visible").slice(4).hide();
        $("#loadMore").show();
        $("#loadless").hide();
        $('html,body').animate({scrollTop: $(this).offset().top},1000);

    });
});
