//script to add skills field in add resume
$(document).ready(function () {
    var maxField = 10; //Input fields increment limitation
    var addButton = $('.btn-added-skill'); //Add button selector
    var wrapper = $('.skill-you-have'); //Input field wrapper
    var fieldHTML = '<div class="skill-you-have-add">' +
        '<div class="form-group ">' +
        '<div class="row">' +
        '<div class="col-md-6">' +
        '<label class="form-control-label">Skill Name</label>' +
        ' <input class="form-control" placeholder="Skill name, e.g. HTML" type="text">' +
        ' </div> <div class="col-md-6">' +
        '  <label class="form-control-label">% (1-100)</label>' +
        ' <input class="form-control" placeholder="Skill proficiency, e.g. 90" type="text">' +
        ' </div>' +
        '  </div>' +
        ' </div>' +
        '<div class=""  style="margin-top:-25px; margin-bottom: 20px;">' +
        '<a href="javascript:void(0);" class=" btn-delete btn-delete-skill"><i class="far fa-trash-alt"></i>Delete This</a>' +
        '</div>' +
        '</div>';
    var x = 1; //Initial field counter is 1

    //Once add button is clicked
    $(addButton).click(function () {
        //Check maximum number of input fields
        if (x < maxField) {
            x++; //Increment field counter
            $(wrapper).append(fieldHTML); //Add field html
        }
    });

    //Once remove button is clicked
    $(wrapper).on('click', '.btn-delete-skill', function () {
        // e.preventDefault();
        $(this).parent().parent().remove(); //Remove field html
        // x--; //Decrement field counter
    });
});


