//checkbox for education qualification
$(document).ready(function () {
    $('#edu-qua').click(function () {
        if ($(this).is(':checked')) {
            $('#education_level,#required_education').attr('disabled',false);
        }
        else
            $('#education_level, #required_education').attr('disabled',true);
            $('#required_education').val('');
    });

});

