$('.RegOption').on('click',function(){
    var option=$(this).data('option');
    window.localStorage.setItem('option', JSON.stringify(option));
});

(function(){
   var returnOption=JSON.parse(window.localStorage.getItem('option')); 
   var values='#'+returnOption;
    if($('.register-tabs').children('a').eq(0).attr('href') == values){
   var job= $('.register-tabs').children('a').eq(0);
   job.tab('show');
   job.siblings().addClass('disabled');
  }
  else{
     var emp= $('.register-tabs').children('a').eq(1);
      emp.tab('show');
      emp.siblings().addClass('disabled');
  }
  localStorage.clear();
})();

$('.register-tabs a').on('click',function(){
    var option=$(this).data('option');
    window.localStorage.setItem('option', JSON.stringify(option));
    var returnOption=JSON.parse(window.localStorage.getItem('option')); 
    var values='#'+returnOption;
    $('.register-tabs a').attr('href',values).tab('show');
    $(this).siblings().addClass('disabled');
});




